import user from "user.json";




export const App = () => {
  return (
    <div
      style={{
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        fontSize: 40,
        color: '#010101'
      }}
    >
      React homework template
    </div>
  );
};

const User = {
  "username": "Jacques Gluke",
  "tag": "jgluke",
  "location": "Ocho Rios, Jamaica",
  "avatar": "https://cdn-icons-png.flaticon.com/512/2922/2922506.png",
  "stats": {
    "followers": 5603,
    "views": 4827,
    "likes": 1308
  }
}

const Profile = (
  <div class="profile">
    <div class="description">
      <img
        src={user.avatar}
        alt={user.username}
        class="avatar"
      />
      <p class="name">{user.username}</p>
      <p class="tag">{user.tag}</p>
      <p class="location">{user.location}</p>
    </div>

    <ul class={user.stats}>
      <li>
        <span class="label">{user.stats.followers}</span>
        <span class="quantity">5603</span>
      </li>
      <li>
        <span class="label">{user.stats.views}</span>
        <span class="quantity">4827</span>
      </li>
      <li>
        <span class="label">{user.stats.likes}</span>
        <span class="quantity">1308</span>
      </li>
    </ul>
  </div>
);

ReactDOM.render(Profile, document.querySelector('#root'));
